styles:
  div.rules-list {
     margin-top: 10px;
  }

  div.rules-list .rules {
     list-style-type: none;
     padding: 0;
  }

div.rules-list li {
  position: relative;
}

div.rules-list li.ko::before {
  content: '\f00d';
  font: normal normal normal 14px/1 FontAwesome;
  width: 32px;
  font-size: 14px;
  display: inline-block;
  text-align: right;
  margin-right: 10px;
  color: #F00;
}

div.rules-list li.ok::before {
  content: '\f00c';
  font: normal normal normal 14px/1 FontAwesome;
  width: 32px;
  font-size: 14px;
  display: inline-block;
  text-align: right;
  margin-right: 10px;
  color: #008000;
}

